package week2;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y = 10, z = 5;
		x = y +z; 
		
		System.out.println("+ operator resulted in " + x);
		
		x = y * z;
		System.out.println("The * operator resulted in " + x);
		
		x = y - z;
		System.out.println("The - operator resulted in " + x);
		
		x = z%y;
		System.out.println("The Modolo operator resulted in " + x);
		
		

	}

}
